import React from 'react';
import { useState } from 'react';
const [text, settext] = useState('')

export default function Textform(props) {
    return (
        <div>


            <div className="mb-3  my-5">
                <label for="exampleFormControlTextarea1" className="form-label">{props.heading}</label>
                <textarea className="form-control" id="exampleFormControlTextarea1" placeholder="Enter your text here" value={text} rows="8"></textarea>
            </div>
        </div>

    )
}
